package com.yqfk.service;

import com.alibaba.fastjson.JSON;
import com.alipay.api.AlipayApiException;
import com.alipay.api.AlipayClient;
import com.alipay.api.DefaultAlipayClient;
import com.alipay.api.request.AlipayTradePagePayRequest;
import com.yqfk.pojo.AlipayBean;
import org.springframework.stereotype.Component;

import java.util.Date;
import java.util.UUID;

@Component
public class Alipay {

    /**
     * 支付接口
     * @param alipayBean
     * @return
     * @throws AlipayApiException
     */

    public String pay(AlipayBean alipayBean,String role) throws AlipayApiException {
        // 1、获得初始化的AlipayClient

        String serverUrl = "https://openapi.alipaydev.com/gateway.do";
        String appId = "2016102500758264";
        String privateKey = "";
        String format = "json";
        String charset = "utf-8";
        String alipayPublicKey = "";
        String signType = "RSA2";
        //支付成功发下面的请求
        String returnUrl = "http://localhost:8006/addOrder";
        String AdminreturnUrl = "http://localhost:8006/admin/addOrder";
        String notifyUrl = "http://localhost:8006/404.html";
        AlipayClient alipayClient = new DefaultAlipayClient(serverUrl, appId, privateKey, format, charset, alipayPublicKey, signType);
        // 2、设置请求参数
        AlipayTradePagePayRequest alipayRequest = new AlipayTradePagePayRequest();
        // 页面跳转同步通知页面路径
        if (role.equals("admin")){
            alipayRequest.setReturnUrl(AdminreturnUrl);
        }else {
            alipayRequest.setReturnUrl(returnUrl);
        }
        // 服务器异步通知页面路径
        alipayRequest.setNotifyUrl(notifyUrl);
        //账户订单号
        String out_trade_no = new Date().getTime()+ UUID.randomUUID().toString();
        alipayBean.setOut_trade_no(out_trade_no);
        // 封装参数
        alipayRequest.setBizContent(JSON.toJSONString(alipayBean));
        System.out.println(JSON.toJSONString(alipayBean));
        // 3、请求支付宝进行付款，并获取支付结果
        String result = alipayClient.pageExecute(alipayRequest).getBody();
        // 返回付款信息
        return result;
    }
}
